from datetime import datetime
from PyQt6.QtGui import QPixmap
from PyQt6.QtCore import QByteArray
import base64

def parse_line(line):
    # Example Line:
    # [03.05.2024 17:37:37] Payed $15.0 to Titan â”ƒ RTLploppey
    # [DD.MM.YYYY HH:MM:SS] ACTION $AMOUNT to RANK ? PLAYER
    # Extracts the timestamp, action, amount and player from a line
    try:
        timestamp, rest = line.split("] ", 1)
        timestamp = timestamp[1:]
        timestamp = datetime.strptime(timestamp, "%d.%m.%Y %H:%M:%S")
        action, rest = rest.split(" $", 1)[0], rest.split(" $", 1)[1]
        amount, rest = rest.split(" ", 1)[0], rest.split(" ", 1)[1]
        player = rest.rsplit(" ", 1)[1]
    except IndexError:
        print("Error parsing line: ", line)
        return None
    
    return {
        "timestamp": timestamp,
        "action": action,
        "amount": amount,
        "player": player,
    }

def ConvertToEuro(amount):
    amount = float(amount)
    amount = (amount/1000000)*5
    return round(amount, 2)

def getAppIcon():
    icon_base64 = ""
    image_data = base64.b64decode(icon_base64)
    byte_array = QByteArray(image_data)
    pixmap = QPixmap()
    pixmap.loadFromData(byte_array)
    return pixmap